function useIncrement (initial, step) {
    const [count, setCount] = useState(initial)
    const increment = () =>{
        setCount(c => c + step)
    }
    return [count, increment]
}

function Compteur () {
    const [count, increment] = useIncrement(0, 2)
    return <button onClick={increment}>Incrémenter{count}</button>

}


